import './App.css';

export default class App extends React.Component {

  state = {
    data: {},
    country:'',
  }


  handleCountryCange

  async componentDidMount() {
    const fetchedData = await fetchData();

    this.setState({
      data: fetchedData
    })

  }


  render() {
    return (
      <div className='container'>
        <Cards data={this.state.data} />
        <CountryPicker />
        <Chart />
      </div>
    );
  }
}
